import {Record} from 'immutable';

import {UPDATE_TEXTAREA_FOCUS, SET_TYPING_STATUS} from './actions';

export const initialState = Record({
  hasTextAreaFocus: false,
  isTyping: false
});

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case UPDATE_TEXTAREA_FOCUS:
      return state.set('hasTextAreaFocus', action.payload.state);
    case SET_TYPING_STATUS:
      return state.set('isTyping', action.payload.isTyping);
    default:
      return state;
  }
}
