export const STORE_SPACE = 'client/STORE_SPACE';
export const SET_ACTIVE_SPACE = 'client/SET_ACTIVE_SPACE';

export function storeSpace(id, space) {
  return {
    type: STORE_SPACE,
    payload: {
      id,
      space
    }
  };
}

export function setActiveSpace(id) {
  return {
    type: SET_ACTIVE_SPACE,
    payload: {
      id
    }
  };
}
