import {createSelector} from 'reselect';

const getClient = (state) => state.client;
const getSpark = (state) => state.spark;

export const getClientDetails = createSelector(
  [getClient, getSpark],
  (client, spark) => ({
    activeSpaceId: client.get('activeSpaceId'),
    openSpaces: client.get('spaces'),
    sparkInstance: spark.get('spark'),
    client
  })
);

export default {};
